local infoBlock = [[{ffffff}
This script allows you to apply new logo styles for Advance RP.

The main commands:
/ linfo - open a menu with basic information.
/ lposition - change the position of the logo on the screen.
/ lstyle [type logo (0 - 3)] [type text (1 - 3)] - change the logo style.
Types logo 0 - off, 1 - default, 2 - new without shadow, 3 - new with color shadow.
Types text 0 - off, 1 - default, 2 - with shadow, 3 - with color shadow.

Posted by Skezz Perry (vk.com/skezzhkg)
]]

local font = renderCreateFont('Calibri', 15, font_flag.BOLD)
local font1 = renderCreateFont('Calibri', 8, font_flag.BOLD)
local sx, sy = ini.settings.x, ini.settings.y
local server = {'', {'RED', 0xffCD0000}, {'GREEN', 0xff228B22}, {'BLUE', 0xff4876FF}, {'LIME', 0x997FFF00}}
local texture = {
    typical = renderLoadTextureFromFileInMemory(memory.strptr(data.typical), 4096),
    advance = renderLoadTextureFromFileInMemory(memory.strptr(data.advance), 4096)
}

function main()
	if not isSampLoaded() or not isSampfuncsLoaded() then return end
	while not isSampAvailable() do wait(100) end
    
    addChat('The script was successfully uploaded {00CC66}/linfo{e6e6fa}. Author {FFCD00}Skezz Perry{e6e6fa}.')
    
    sampRegisterChatCommand('linfo', function()
        sampShowDialog(1, '{FFCD00}It`s logo?', infoBlock, 'Close', '', 0)
    end)
    
    sampRegisterChatCommand('lposition', function()
        if not renderSettings then
            addChat('Using the cursor, set the logo to the desired location.')
            addChat('To save, {00CC66}enter the command again{e6e6fa} or {00CC66}press the LButton{e6e6fa}.')
            renderSettings = true
        else
            addChat('You have {00CC66}successfully saved{e6e6fa} the new position of the logo.')
            ini.settings.x, ini.settings.y = sx, sy
            inicfg.save(ini, 'Logos global settings.ini')
            sampSetCursorMode(0)
            renderSettings = nil
        end
    end)
    
    sampRegisterChatCommand('lstyle', function(parametrs)
        if string.match(parametrs, '(%d+) (%d+)') then
            local ltype, ttype = string.match(parametrs, '(%d+) (%d+)')
            local ltype, ttype = tonumber(ltype), tonumber(ttype)
            
            if (ttype >= 0 and ttype <= 3) and (ltype >= 0 and ltype <= 3) then
                ini.settings.lstyle = ltype
                ini.settings.tstyle = ttype
                inicfg.save(ini, 'Logos global settings.ini')
                addChat('You have {00CC66}successfully saved{e6e6fa} the new style.')
            else addChat('Enter the required parameters for /lstyle [{00CC66}type logo (0 or 3){e6e6fa}] [{00CC66}type text  (1 or 3){e6e6fa}].') end
        else addChat('Enter the required parameters for /lstyle [{00CC66}type logo (0 or 3){e6e6fa}] [{00CC66}type text (1 or 3){e6e6fa}].') end
    end)
    
    lua_thread.create(render)
    
    while true do wait(0) end
end

function render()
	if string.match(sampGetCurrentServerAddress(), '54.37.142.7(%d)') then
		serverId = string.match(sampGetCurrentServerAddress(), '54.37.142.7(%d)')
		textLogo = 'ROLE PLAY GAMING'-- .. server[tonumber(serverId)][1]
		fix = renderGetFontDrawTextLength(font1, textLogo)
		posLogoX = (sx + 98.5) - fix / 2
    else
        delayRender = true
	end
    
    while true do wait(0)
        if sampIsLocalPlayerSpawned() then
            if destroyAndUpdate then
                if string.match(sampGetCurrentServerAddress(), '54.37.142.7(%d)') then
                    serverId = string.match(sampGetCurrentServerAddress(), '54.37.142.7(%d)')
                    textLogo = 'ROLE PLAY GAMING'-- .. server[tonumber(serverId)][1]
                    
                    fix = renderGetFontDrawTextLength(font1, textLogo)
                    posLogoX = (sx + 98.5) - fix / 2
                    destroyAndUpdate = nil
                    delayRender = nil
                else
                    delayRender = true
                end
            elseif not delayRender then
                if renderSettings then
                    sx, sy = getCursorPos()
                    posLogoX = (sx + 98.5) - fix / 2
                    renderLineBox(sx - 5, sy - 3, 153, 60, 1, server[tonumber(serverId)][2])
                    
                    if not sampIsCursorActive() then
                        sampSetCursorMode(2)
                    end
                    
                    if wasKeyPressed(0x01) then
                        addChat('You have {00CC66}successfully saved{e6e6fa} the new position of the logo.')
                        ini.settings.x, ini.settings.y = sx, sy
                        inicfg.save(ini, 'Logos global settings.ini')
                        sampSetCursorMode(0)
                        renderSettings = nil 
                    end
                end
                
                if ini.settings.lstyle == 1 then
                    renderDrawTexture(texture.advance, sx, sy, 50, 50, 0, 0xffFFFFFF)
                elseif ini.settings.lstyle == 2 then
                    renderDrawTexture(texture.typical, sx + 2, sy + 1, 50, 52, 0, 0xff111111)
                    renderDrawTexture(texture.typical, sx, sy, 50, 52, 0, 0xffffffff)
                elseif ini.settings.lstyle == 3 then
                    renderDrawTexture(texture.typical, sx + 2, sy + 1, 50, 52, 0, server[tonumber(serverId)][2])
                    renderDrawTexture(texture.typical, sx, sy, 50, 52, 0, 0xffffffff)
                end
                
                if ini.settings.tstyle == 1 then
                    renderFontDrawText(font, 'ADVANCE', sx + 57, sy + 7, 0xFFFFFFFF)
                    renderFontDrawText(font1, textLogo, posLogoX, sy + 25, 0xFFFFFFFF)
                elseif ini.settings.tstyle == 2 then
                    renderFontDrawText(font, 'ADVANCE', sx + 58.5, sy + 8, 0xff111111)
                    renderFontDrawText(font1, textLogo, posLogoX + 1, sy + 25.5, 0xff111111)
                    renderFontDrawText(font, 'ADVANCE', sx + 57, sy + 7, 0xFFFFFFFF)
                    renderFontDrawText(font1, textLogo, posLogoX, sy + 25, 0xFFFFFFFF)
                elseif ini.settings.tstyle == 3 then
                    renderFontDrawText(font, 'ADVANCE', sx + 58.5, sy + 8, server[tonumber(serverId)][2])
                    renderFontDrawText(font1, textLogo, posLogoX + 1, sy + 25.5, server[tonumber(serverId)][2])
                    renderFontDrawText(font, 'ADVANCE', sx + 57, sy + 7, 0xFFFFFFFF)
                    renderFontDrawText(font1, textLogo, posLogoX, sy + 25, 0xFFFFFFFF)
                end
            end
        end
    end
end

function addChat(text)
    sampAddChatMessage(string.format('It`s logo?{e6e6fa} - %s', tostring(text)), 0xffF08080)
end

function renderLineBox(x, y, w, h, scale, color)
    renderDrawLine(x, y, x + w, y, scale, color)
    renderDrawLine(x, y + h, x + w, y + h, scale, color)
    renderDrawLine(x, y, x, y + h, scale, color)
    renderDrawLine(x + w, y, x + w, y + h, scale, color)
end

function isKeyCheckAvailable()
    if not isSampfuncsLoaded() then return not isPauseMenuActive() end
    local result = not isSampfuncsConsoleActive() and not isPauseMenuActive()
    if isSampLoaded() and isSampAvailable() then 
        result = result and not sampIsChatInputActive() and not sampIsDialogActive()
    end
    return result
end

function tobool(value)
    if tonumber(value) == 1 then return false else return true end
end

function sampev.onSendSpawn()
    destroyAndUpdate = true
end

function sampev.onShowTextDraw(id)
    if not delayRender then
        if id >= 188 and id <= 201 then
            return false
        end
    end
end

function onScriptTerminate(script, bool)
	if thisScript() == script then
		sampSetCursorMode(0)
	end
end
